package ;
import ;

import org.adligo.xml_io.client.I_NamespaceConverters;
import org.adligo.xml_io.client.NamespaceConverters;
import org.adligo.xml_io.client.NamespaceConvertersMutant;

public class ConverterSetup {
	public static final String NAMESPACE = "";
	public static final String NAMESPACE_VERSION = "";
	public static final String PACKAGE_NAME = "";

	public static I_NamespaceConverters setUp() {
		NamespaceConvertersMutant converters = new NamespaceConvertersMutant();
		converters.setNamespace(NAMESPACE);
		converters.setPackageName(PACKAGE_NAME);


		converters.addXmlToObjectConverter(.TAG_NAME, new ());
		converters.addObjectToXmlConverter(.class, new ());


		converters.addXmlAttributeToObjectConverter(.class, new ());

		return new NamespaceConverters(converters);
	}
}
